    """
    Return the nth Fibonacci number with F(0)=0, F(1)=1.
    Uses the fast-doubling method in O(log n) time.

    Examples:
      fib(0) -> 0
      fib(1) -> 1
      fib(10) -> 55
    """
    if n < 0:
        raise ValueError("n must be a non-negative integer")

    def _fib(k: int) -> tuple[int, int]:
        if k == 0:
            return 0, 1
        a, b = _fib(k >> 1)     # a = F(m), b = F(m+1) where m = k//2
        c = a * ((b << 1) - a)  # F(2m)
        d = a * a + b * b       # F(2m+1)
        if k & 1:
            return d, c + d     # (F(2m+1), F(2m+2))
        else:
            return c, d         # (F(2m),   F(2m+1))

    return _fib(n)[0]
